mapper.select:


select p.product_no, p.title, p.product_name, p.now_price, p.post_image, p.member_id, m.name, p.bid_end_time
from(
	select row_number() over(order by ${sortBy}) as rnum,product_no,title,product_name,now_price,bid_end_time,post_image,member_id
	from post
	where product_name like '${category}%'
) p, a_member m
where p.member_id=m.member_id and rnum between #{startRowNumber} and #{endRowNumber}

select qna_no, to_char(qna_time,'YYYY-MM-DD HH24:MI:SS') as qna_time, qna_type, qna_content, member_id
from qna
where product_no=#{value}
